const readline = require('readline');


//criando uma intancia de interface de leitura e escrita
const rl = readline.createInterface({
input: process.stdin,
output: process.stdout
});

//solicitando ao usuario que insira sua idade
rl.question('digite um numero',(numero1)=>{
    rl.question('digite outro numero',(numero2)=>{

const resultado = Number(numero1) + Number(numero2);


console.log(`a soma de ${numero1} e ${numero2} Ã© ${resultado}`);
rl.close();

})})